import React, { Component } from 'react'
import axios from 'axios';
import InfiniteScroll from 'react-infinite-scroll-component'
import Image from './Image'

export class Images extends Component {

    state = {
        images:[],
        count: 30,
        start: 1
    }

    componentDidMount(){
        const { count, start } = this.state;
        axios.get(`/api/photos?count=${count}&start=${start}`).then(res => this.setState({ images: res.data }))
    }

    fetchImages = () => {
        const { count, start } = this.state;
        this.setState({start: this.state.start + count})
        axios.get(`/api/photos?count=${count}&start=${start}`).then(res => this.setState({ images: res.data }))
    }

    render() {
        console.log(this.state)
        return (
            <div className="images">
                <InfiniteScroll dataLength={this.state.images} next={this.fetchImages} hasMore={true} loader={<h4>Loading....</h4>}>
                    {this.setState.state.images.map(image => {
                        <Image />
                    })}
                </InfiniteScroll>                
            </div>
        )
    }
}

export default Images
